void Context_EventA(Context *me, EventAData *data) {
  State nextState;
  nextState = me->pState->pEventA(data);
  if (nextState != me->pState) {
    me->pState->pExit();
    me->pState = getStateActionSet(nextState);
    me->pState->pEntry();
  }
}

