int insert(MessageQueue* me, Message m) {
  OS_lock_semaphore(me->sema);
  if (!isFull(me)) {
    me->data[me->head] = m;
    me->head = getNextIndex(me->head);
    ++me->size;
    OS_release_semaphore(me->sema);
    return 1;
  }
  OS_release_semaphore(me->sema);
  return 0;
}

